DC_blockFIR_filterType *DC_blockFIR_filter_create( void )
{
    DC_blockFIR_filterType *result = (DC_blockFIR_filterType *)malloc( sizeof( DC_blockFIR_filterType ) );  // Allocate memory for the object
    DC_blockFIR_filter_init( result );                                          // Initialize it
    return result;                                                              // Return the result
}

void DC_blockFIR_filter_destroy( DC_blockFIR_filterType *pObject )
{
    free( pObject );
}

 void DC_blockFIR_filter_init( DC_blockFIR_filterType * pThis )
{
    DC_blockFIR_filter_reset( pThis );

}

 void DC_blockFIR_filter_reset( DC_blockFIR_filterType * pThis )
{
    memset( &pThis->state, 0, sizeof( pThis->state ) ); // Reset state to 0
    pThis->pointer = pThis->state;                      // History buffer points to start of state buffer
    pThis->output = 0;                                  // Reset output

}

 int DC_blockFIR_filter_filterBlock( DC_blockFIR_filterType * pThis, float * pInput, float * pOutput, unsigned int count )
{
    float *pOriginalOutput = pOutput;   // Save original output so we can track the number of samples processed
    float accumulator;

    for( ;count; --count )
    {
        pThis->pointer[DC_blockFIR_filter_length] = *pInput;                        // Copy sample to top of history buffer
        *(pThis->pointer++) = *(pInput++);                                      // Copy sample to bottom of history buffer

        if( pThis->pointer >= pThis->state + DC_blockFIR_filter_length )                // Handle wrap-around
            pThis->pointer -= DC_blockFIR_filter_length;

        accumulator = 0;
        DC_blockFIR_filter_dotProduct( pThis->pointer, DC_blockFIR_filter_coefficients, &accumulator, DC_blockFIR_filter_length );
        *(pOutput++) = accumulator; // Store the result
    }

    return pOutput - pOriginalOutput;

}

 void DC_blockFIR_filter_dotProduct( float * pInput, float * pKernel, float * pAccumulator, short count )
{
    float accumulator = *pAccumulator;
    while( count-- )
        accumulator += ((float)*(pKernel++)) * *(pInput++);
    *pAccumulator = accumulator;

}


